mapper.delete:

delete from t_duty
where EMPLOYEE_ID = #{employeeId,jdbcType=CHAR}
  and DUTY_MONTH = #{dutyMonth,jdbcType=CHAR}
  and DUTY_WORK_TIME_FROM = #{dutyWorkTimeFrom,jdbcType=TIMESTAMP}

mapper.update:

update t_duty
set DUTY_DATE = #{dutyDate,jdbcType=CHAR}
where EMPLOYEE_ID = #{employeeId,jdbcType=CHAR}
  and DUTY_MONTH = #{dutyMonth,jdbcType=CHAR}
  and DUTY_WORK_TIME_FROM = #{dutyWorkTimeFrom,jdbcType=TIMESTAMP}

mapper.select:

select EMPLOYEE_ID, DUTY_MONTH, DUTY_WORK_TIME_FROM, DUTY_DATE
from t_duty
where EMPLOYEE_ID = #{employeeId,jdbcType=CHAR}
  and DUTY_MONTH = #{dutyMonth,jdbcType=CHAR}
  and DUTY_WORK_TIME_FROM = #{dutyWorkTimeFrom,jdbcType=TIMESTAMP}
